  // Select all buttons with the class "col" and "btn-open-row"
var openButtons = document.querySelectorAll(".col .btn-open-row");

// Loop through each button
openButtons.forEach(function (openButton) {

    // Change the innerHTML of each button
    openButton.innerHTML = '<a href="/enrolled-couses-and-units"> Explore</a>';

    // Add a click event handler
    openButton.addEventListener("click", function (event) {
        event.preventDefault(); // Prevent default link behavior
         // Find the closest parent row with the class "data-row"
        var row = openButton.closest('.data-row');


            // Get the third column (cell) value
            var thirdColumnValue = row.querySelectorAll('.col')[2].textContent;

            // Do something with the third column value
            console.log(thirdColumnValue);
            window.location.href = "/enrolled-couses-and-units/"+thirdColumnValue;

      //  console.log("hello");
        //window.location.href = "/topics-and-content";
    });
});
