frappe.web_form.after_load = () => {
    if (window.location.pathname.endsWith("/new") && frappe.session.user) {
        // Query the Student doctype to find the student with the matching email
        frappe.call({
            method: 'frappe.client.get_value',
            args: {
                doctype: 'Student',
                filters: {
                    'user': frappe.session.user
                },
                fieldname: ['name']  // You can add more fields as needed
            },
            callback: function(response) {
                if (response.message) {
                    let studentName = response.message.name;
                    if (studentName) {
                        let current_path = window.location.href;
                        window.location.href = current_path.replace("/new", "/" + studentName);
                    }
                }
            }
        });
    }
};

