frappe.web_form.on('custom_branch', function(value) {
    
    const selectedBranch = typeof value === 'string' ? value : value?.value;
    console.log('Selected Branch:', selectedBranch);

    // Reset Program selection when branch changes
    frappe.web_form.set_value('program', '');

    const programField = frappe.web_form.fields_dict['program'];
    if (!programField) return;

    // Fetch programs via server-side method
    frappe.call({
        method: "education_custom.api.program.get_programs_by_branch", // path to your server method
        args: { branch: selectedBranch },
        callback: function(r) {
            if (!r.message) return;

            console.log("Filtered Programs:", r.message);
            frappe.web_form.set_value('program', ''); // clear previous selection
            // Set options dynamically
            const options = [''].concat(r.message);
                frappe.web_form.fields_dict.program.set_data(options);

        }
    });
});

frappe.web_form.after_load = () => {
    console.log('loaded');

    // Function to get fee structure ID from student applications
    const getFeeStructureFromStudentApplications = (program) => {
        return new Promise((resolve) => {
            // Get current student from web form
            const studentName = frappe.web_form.get_value('student_applicant_name') || 
                               frappe.web_form.get_value('student');
            
            if (!studentName) {
                resolve(null);
                return;
            }

            // Search student applications for this program with fee structure
            frappe.call({
                method: "frappe.client.get_list",
                args: {
                    doctype: "Student Applicant",
                    filters: { 
                        student: studentName,
                        program: program,
                        fee_structure: ["!=", ""]
                    },
                    fields: ["fee_structure"],
                    limit_page_length: 1,
                    order_by: "creation desc"
                },
                callback: function (r) {
                    if (r.message && r.message.length > 0 && r.message[0].fee_structure) {
                        resolve(r.message[0].fee_structure);
                    } else {
                        resolve(null);
                    }
                }
            });
        });
    };

    // ----------------------------
    // HEADER
    // ----------------------------
    const headerCell = document.querySelector(
        '.grid-heading-row [data-fieldname="applicant_naming_series"]'
    );
    if (headerCell) {
        const staticArea = headerCell.querySelector('.static-area');
        if (staticArea) {
            staticArea.textContent = 'Fees | Apply';
        }
    }

    // ----------------------------
    // ROWS
    // ----------------------------
    const namingCells = document.querySelectorAll(
        '.data-row [data-fieldname="applicant_naming_series"]'
    );

    namingCells.forEach(cell => {
        // Check if buttons already exist
        if (cell.querySelector('.btn-course-fees')) {
            return; // Skip if buttons already exist
        }

        // Clear any existing content
        const staticArea = cell.querySelector('.static-area');
        if (staticArea) staticArea.innerHTML = '';

        // Hide input/field area
        const fieldArea = cell.querySelector('.field-area');
        if (fieldArea) fieldArea.style.display = 'none';

        // Add buttons container
        const buttonsContainer = document.createElement('div');
        buttonsContainer.style.display = 'flex';
        buttonsContainer.style.gap = '5px';

        const feesBtn = document.createElement('a');
        feesBtn.href = '#';
        feesBtn.textContent = 'Fees';
        feesBtn.className = 'btn-course-fees';

        const applyBtn = document.createElement('a');
        applyBtn.href = '#';
        applyBtn.textContent = 'Apply';
        applyBtn.className = 'btn-apply';

        buttonsContainer.appendChild(feesBtn);
        buttonsContainer.appendChild(applyBtn);
        cell.appendChild(buttonsContainer);

        // Get program from row
        const row = cell.closest('.data-row');
        const program = row.querySelectorAll('.col')[2].textContent.trim();
        
        let isLoading = false;

        // ---------- FEES CLICK ----------
        feesBtn.addEventListener('click', async function (event) {
            event.preventDefault();
            
            if (isLoading) return;
            
            const academicYear = frappe.web_form.get_value('academic_year');
            const academicTerm = frappe.web_form.get_value('custom_academic_term');

            if (!program || !academicYear || !academicTerm) {
                frappe.msgprint("Please select Program, Academic Year and Academic Term");
                return;
            }

            try {
                isLoading = true;
                feesBtn.textContent = 'Loading...';
                
                // Step 1: Try direct fee structure search
                let feeStructureId = null;
                
                const directResult = await new Promise((resolve) => {
                    frappe.call({
                        method: "frappe.client.get_list",
                        args: {
                            doctype: "Fee Structure",
                            filters: { program, academic_year: academicYear, academic_term: academicTerm },
                            fields: ["name"],
                            limit_page_length: 1
                        },
                        callback: function (r) {
                            if (r.message?.length) {
                                resolve(r.message[0].name);
                            } else {
                                resolve(null);
                            }
                        }
                    });
                });

                if (directResult) {
                    feeStructureId = directResult;
                } else {
                    // Step 2: Try to get from student applications
                    feeStructureId = await getFeeStructureFromStudentApplications(program);
                }

                // Redirect if found
                if (feeStructureId) {
                    window.location.href = "/fee-structure/" + encodeURIComponent(feeStructureId);
                } else {
                    frappe.msgprint("No Fee Structure found");
                }
                
            } catch (error) {
                console.error("Error:", error);
                frappe.msgprint("An error occurred while fetching fee structure");
            } finally {
                isLoading = false;
                feesBtn.textContent = 'Fees';
            }
        });

        // ---------- APPLY CLICK ----------
        applyBtn.addEventListener('click', function (event) {
            event.preventDefault();
            if (!program) {
                frappe.msgprint("Program not found");
                return;
            }
             // Store program in localStorage
    localStorage.setItem('selectedProgram', program);
    
    // Optional: Also store with timestamp for expiration
    const programData = {
        value: program,
        timestamp: new Date().getTime()
    };
    localStorage.setItem('programData', JSON.stringify(programData));
    sessionStorage.setItem('selectedProgram', program);
    console.log("Program stored in localStorage:", program);
            window.location.href = "/student-application-form?program=" + encodeURIComponent(program);
        });
    });
};

